import { useState, useEffect } from "react";
import { Col } from "react-bootstrap";
import 'animate.css';
import meImg from "../assets/img/meImg.png";
import TrackVisibility from 'react-on-screen';
export const Newsletter = () => {

  const [loopNum, setLoopNum] = useState(0);
  const [isDeleting, setIsDeleting] = useState(false);
  const [text, setText] = useState('');
  const [delta, setDelta] = useState(300 - Math.random() * 100);
  const [index, setIndex] = useState(1);
  const toRotate = [ "Olá, sou a Jezebel!" ];
  const period = 2000;

  useEffect(() => {
    let ticker = setInterval(() => {
      tick();
    }, delta);

    return () => { clearInterval(ticker) };
  }, [text])

  const onButtonClick = () => {
    fetch('Jezebel_Guedes.pdf').then(response => {
      response.blob().then(blob => {
          const fileURL = window.URL.createObjectURL(blob);
        let alink = document.createElement('a');
        alink.href = fileURL;
        alink.download = 'Jezebel_Guedes.pdf';
        alink.click();
    })
})


  }


  const tick = () => {
    let i = loopNum % toRotate.length;
    let fullText = toRotate[i];
    let updatedText = isDeleting ? fullText.substring(0, text.length - 1) : fullText.substring(0, text.length + 1);

    setText(updatedText);

    if (isDeleting) {
      setDelta(prevDelta => prevDelta / 2);
    }

    if (!isDeleting && updatedText === fullText) {
      setIsDeleting(true);
      setIndex(prevIndex => prevIndex - 1);
      setDelta(period);
    } else if (isDeleting && updatedText === '') {
      setIsDeleting(false);
      setLoopNum(loopNum + 1);
      setIndex(1);
      setDelta(500);
    } else {
      setIndex(prevIndex => prevIndex + 1);
    }
  }

  return (
    
    <Col lg={12}>
    <div className="newsletter-bx wow slideInUp">
    <TrackVisibility>
              {({ isVisible }) =>
                <div className={isVisible ? "animate__animated animate__zoomIn" : ""}>
                  <img src={meImg} alt="Me Img"/>
                </div>}
            </TrackVisibility>  
     
          <Col xs={12} md={12} xl={6}>
            <TrackVisibility>
              {({ isVisible }) =>
              <div className={isVisible ? "animate__animated animate__fadeIn" : ""}>
               <div className="about">
                <h1>{`Sobre mim`} </h1>
                  <h5>Olá, me chamo Jezebel. Sou bacharel em Tecnologia da Informação pela Univesp, Desenvolvedora Web e  UX Designer. Acredito que o  uso de tecnologias em onjunto com design centrado no usuário é solução de  qualquer sucesso.</h5>
                  
                  <div className="new-email-bx">
                  <h3><button onClick={onButtonClick}>Baixar Currículo</button></h3>
                  </div>
              </div>
              </div>
              }
            </TrackVisibility>
            
         
          </Col>
         
      </div>
      
          
          </Col>
  )
   }
