import { useState } from "react";
import { Slide } from "react-awesome-reveal";
import { Container, Row, Col } from "react-bootstrap";
import contactImg from "../assets/img/contact-img.png";
import emailjs from "emailjs-com";
import 'animate.css';

export const Contact = () => {

  const [val, setVal] = useState(("contact_send"));
  const btn = document.getElementById("button");

  const handleSubmit = async (e) => {
    e.preventDefault();
   setVal(("contact_send"));
   emailjs
      .sendForm(
         "service_p3jpunc",
         "template_5whufbp",
         e.target,
         "dldo5QNKt9BDCl0ro"
      )
      .then(() => {
        setVal(("contact_sent"));
        btn.style.background = "green";
        clearFields();
        setTimeout(() => {
          setVal(("contact_send"));
          btn.style.background = "white";
        }, 2000);
      })
      .catch((err) => {
        setTimeout(() => {
          setVal(("contact_senderr"));
          btn.style.background = "red";
        }, 3000);
      });
  };
  const [email, setEmail] = useState("");
  const [firstName, setFirstName] = useState("");
  const [secName, setSecName] = useState("");
  const [number, setNumber] = useState("");
  const [message, setMessage] = useState("");
  const clearFields = () => {
    setFirstName("");
    setSecName("");
    setNumber("");
    setEmail("");
    setMessage("");
  };


  return (
    <section className="contact" id="connect">
      <Container>
        <Row className="align-items-center">
          <Col size={12} md={6}>
          <Slide direction="left" triggerOnce="true"  >
            <img className="animate__animated animate__zoomIn"  src={contactImg} alt="Contact Us"/>
            </Slide>
          </Col>
          <Col size={12} md={6}>
                <div className="animate__animated animate__fadeIn">
                <h2>Deseja Entrar em Contato?</h2>
                <Slide direction="right" triggerOnce="true">
                <form onSubmit={handleSubmit}>
                  <Row>
                    <Col size={12} sm={6} className="px-1">
                    <input
                        value={firstName}
                        onChange={(e) => setFirstName(e.target.value)}
                        type="text"
                        required
                        placeholder={("contact_first")}
                        name="name"
                        className="form-int"
                      />
                    </Col>
                    <Col size={12} sm={6} className="px-1">
                      <input
                        value={secName}
                        onChange={(e) => setSecName(e.target.value)}
                        type="text"
                        placeholder={("contact_last")}
                        name="name"
                        className="form-int"
                        required
                      />
                    </Col>
                    <Col size={12} sm={6} className="px-1">
                      <input
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        type="email"
                        placeholder={("contact_em")}
                        name="user_email"
                        className="form-int"
                        required
                      />
                    </Col>
                    <Col size={12} sm={6} className="px-1">
                      <input
                        value={number}
                        onChange={(e) => setNumber(e.target.value)}
                        type="tel"
                        placeholder={("contact_num")}
                        name="phone"
                        className="form-int"
                      />
                    </Col>
                    <Col size={12} className="px-1">
                      <textarea
                        value={message}
                        onChange={(e) => setMessage(e.target.value)}
                        rows="6"
                        placeholder={("contact_mes")}
                        name="message"
                      ></textarea>
                      <input
                        type="submit"
                        id="button"
                        className="submit"
                        value={val}
                      />
                    </Col>
                  </Row>
                </form>
              </Slide>
            </div>
          </Col>
        </Row>
      </Container>
    </section>
  );
};
